import React, { useEffect } from 'react';

const App = () => {
  useEffect(() => {
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.ready
        .then((registration) => {
          if (!registration.active) {
            return navigator.serviceWorker.register('/sw.js');
          }
          return registration;
        })
        .then(async (registration) => {
          console.log('Service Worker registrado ou já ativo:', registration);

          // Após o registro do Service Worker, inscreva-se para notificações push
          const subscription = await subscribeToPushNotifications(registration);
          console.log('Inscrição concluída:', subscription);

          // Envie a inscrição para o servidor
          await sendSubscriptionToServer(subscription);
        })
        .catch((error) => {
          console.error('Erro ao registrar o Service Worker:', error);
        });
    }
  }, []); // Apenas na primeira renderização

  return <div className="App">Aplicativo React com Notificações Push!</div>;
};

// Função para inscrever o cliente em notificações push
const subscribeToPushNotifications = async (registration) => {
  try {
    const publicKeyResponse = await fetch('/vapidPublicKey');
    const vapidPublicKey = await publicKeyResponse.text();

    // Transformar a chave pública em um Uint8Array
    const convertedVapidKey = urlBase64ToUint8Array(vapidPublicKey);

    // Criar a inscrição
    const subscription = await registration.pushManager.subscribe({
      userVisibleOnly: true,
      applicationServerKey: convertedVapidKey,
    });

    return subscription;
  } catch (error) {
    console.error('Erro ao se inscrever para notificações push:', error);
    throw error;
  }
};

// Função para enviar a inscrição ao servidor
const sendSubscriptionToServer = async (subscription) => {
  try {
    await fetch('/subscribe', {
      method: 'POST',
      body: JSON.stringify(subscription),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    console.log('Inscrição enviada ao servidor com sucesso.');
  } catch (error) {
    console.error('Erro ao enviar a inscrição ao servidor:', error);
    throw error;
  }
};

// Função para converter a chave pública para Uint8Array
const urlBase64ToUint8Array = (base64String) => {
  const padding = '='.repeat((4 - (base64String.length % 4)) % 4);
  const base64 = (base64String + padding).replace(/-/g, '+').replace(/_/g, '/');
  const rawData = atob(base64);
  return Uint8Array.from([...rawData].map((char) => char.charCodeAt(0)));
};

export default App;
