import React, { useEffect } from 'react';




// Função para enviar a inscrição ao servidor
const sendSubscriptionToServer = async (subscription) => {
  try {
    await fetch('http://localhost:3333/subscribe', {
      method: 'POST',
      body: JSON.stringify(subscription),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    console.log('Inscrição enviada ao servidor com sucesso.');
  } catch (error) {
    console.error('Erro ao enviar a inscrição ao servidor:', error);
    throw error;
  }
};

const App = () => {

  useEffect(() => {
    // Função para inscrever o cliente em notificações push
    const subscribeToPushNotifications = async (registration) => {
      try {
        const vapidPublicKey = 'BDAFRkRYA-h4r0SFyAFvufV2JCeLE-aDXk8mq9BFZ2yPJsXjqvK2MJu2ilPwo9FDdjd6hdAZfrSs2qO0bXJSe1w';

        // Criar a inscrição
        const subscription = await registration.pushManager.subscribe({
          userVisibleOnly: true,
          applicationServerKey: vapidPublicKey,
        });
        console.log(subscription);
        sendSubscriptionToServer(subscription);

      } catch (error) {
        console.error('Erro ao se inscrever para notificações push:', error);
        throw error;
      }
    };

    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.register('/sw.js')
      .then((registration) => {
        console.log('Service Worker registrado com sucesso:', registration);
        subscribeToPushNotifications(registration);
      })
      .catch((error) => {
        console.error('Erro ao registrar o Service Worker:', error);
      });
    } else {
      console.error('No serviceWorker in navigator');
    }
  }, []);

  return <div className="App">Aplicativo React com Notificações Push!</div>;
};

export default App;
